function rotateImages(){let e=0;setInterval(()=>{switch(e){case 0:image1.style.setProperty("opacity","0"),image2.style.setProperty("opacity","1"),e++;break;case 1:image2.style.setProperty("opacity","0"),image3.style.setProperty("opacity","1"),e++;break;case 2:image3.style.setProperty("opacity","0"),image1.style.setProperty("opacity","1"),e=0;break;default:e=0}},7e3)}let image1=document.querySelector("#image-1"),image2=document.querySelector("#image-2"),image3=document.querySelector("#image-3");rotateImages();var sections=document.querySelectorAll(".split-section");const callback=function(e){e.forEach(e=>{e.isIntersecting&&e.target.style.setProperty("animation","fadeInUp 1.1s ease forwards")})};let options={threshold:.75};const sectionObserver=new IntersectionObserver(callback,options);let heroText=document.querySelector("#hero-text"),linkBlock=document.querySelector("#link-block");const heroTextObserver=new IntersectionObserver(function(e){e.forEach(e=>{e.isIntersecting&&e.target.style.setProperty("animation","fadeInUp 1.1s ease 500ms forwards")})},{threshold:.25}),linkBlockObserver=new IntersectionObserver(function(e){e.forEach(e=>{e.isIntersecting&&e.target.style.setProperty("animation","fadeInUp 1.1s ease 500ms forwards")})},{threshold:.25});window.addEventListener("load",()=>{sections.forEach(e=>{sectionObserver.observe(e)}),heroTextObserver.observe(heroText),linkBlockObserver.observe(linkBlock),document.body.classList.add("page-loaded")});