let image1=document.querySelector("#image-1"),image2=document.querySelector("#image-2"),image3=document.querySelector("#image-3");function rotateImages(){let counter=0;setInterval(()=>{switch(counter){case 0:image1.style.setProperty("opacity","0"),image2.style.setProperty("opacity","1"),counter++;break;case 1:image2.style.setProperty("opacity","0"),image3.style.setProperty("opacity","1"),counter++;break;case 2:image3.style.setProperty("opacity","0"),image1.style.setProperty("opacity","1"),counter=0;break;default:counter=0}},7e3)}rotateImages();