let image1=document.querySelector("#image-1"),image2=document.querySelector("#image-2"),image3=document.querySelector("#image-3");function rotateImages(){let counter=0;setInterval(()=>{switch(counter){case 0:image1.style.setProperty("opacity","0"),image2.style.setProperty("opacity","1"),counter++;break;case 1:image2.style.setProperty("opacity","0"),image3.style.setProperty("opacity","1"),counter++;break;case 2:image3.style.setProperty("opacity","0"),image1.style.setProperty("opacity","1"),counter=0;break;default:counter=0}},7e3)}rotateImages();var sections=document.querySelectorAll(".split-section");const callback=function(entries){entries.forEach(entry=>{entry.isIntersecting&&entry.target.style.setProperty("animation","fadeInUp 1s forwards cubic-bezier(0.31,0.87,0.32,1)")})};let options={threshold:.75};const sectionObserver=new IntersectionObserver(callback,options);let heroText=document.querySelector("#hero-text"),linkBlock=document.querySelector("#link-block");const heroTextObserver=new IntersectionObserver((function(entries){entries.forEach(entry=>{entry.isIntersecting&&entry.target.style.setProperty("animation","fadeInUp 1s 500ms forwards cubic-bezier(0.31,0.87,0.32,1)")})}),{threshold:.25}),linkBlockObserver=new IntersectionObserver((function(entries){entries.forEach(entry=>{entry.isIntersecting&&entry.target.style.setProperty("animation","fadeInUp 1s 500ms forwards cubic-bezier(0.31,0.87,0.32,1)")})}),{threshold:.25});window.addEventListener("load",()=>{sections.forEach(section=>{sectionObserver.observe(section)}),heroTextObserver.observe(heroText),linkBlockObserver.observe(linkBlock)});